hi Nh\xE1nh 2: 196 Nguy\u1EC5n Tri Ph\u01B0\u01A1ng, Th\xE0nh ph\u1ED1 Bi\xEAn H\xF2a, \u0110\u1ED3ng Nai"}),/*#__PURE__*/_jsx(Box,{style:{fontSize:"14px",float:"left",marginLeft:"-2px",textAlign:"left",margin:"10px 0px",letterSpacing:0.5,lineHeight:1.8},children:"Chi Nh\xE1nh 3: M\u1EF9 Ph\u01B0\u1EDBc T\xE2n V\u1EA1n, TP. Th\u1EE7 D\u1EA7u M\u1ED9t, B\xECnh D\u01B0\u01A1ng"}),/*#__PURE__*/_jsx(Box,{style:{fontSize:"14px",float:"left",marginLeft:"-2px",textAlign:"left",letterSpacing:0.5,lineHeight:1.8},children:"Chi Nh\xE1nh 4: KCN \u0110\u1EE9c H\xF2a \u2013 Long An"})]})]}),/*#__PURE__*/_jsxs(Box,{display:'flex',flexDirection:'column',sx:{padding:"0px 240px"},children:[/*#__PURE__*/_jsx(Box,{sx:{fontSize:"16px",fontFamily:"Roboto",fontWeight:"bold",color:"white",float:"left"},children:"\u0110\u01AF\u1EDCNG D\u1EAAN NHANH"}),/*#__PURE__*/_jsx(Divider,{style:{border:"1px solid white",marginTop:"20px",width:"370px",float:"left"}}),/*#__PURE__*/_jsxs(Box,{sx:{fontFamily:"Roboto",float:"left",display:"flex",flexDirection:"column"},children:[/*#__PURE__*/_jsx(Box,{component:Link,to:'/',style:{fontSize:"14px",float:"left",marginLeft:"-2px",textAlign:"left",margin:"20px 0px",letterSpacing:0.5,textDecoration:"none",color:"white"},children:"TRANG CH\u1EE6"}),/*#__PURE__*/_jsx(Box,{component:Link,to:'/LoaiSP',style:{fontSize:"14px",float:"left",textAlign:"left",letterSpacing:0.5,textDecoration:"none",color:"white"},children:"S\u1EA2N PH\u1EA8M"}),/*#__PURE__*/_jsx(Box,{style:{fontSize:"14px",float:"left",marginLeft:"-2px",textAlign:"left",margin:"20px 0px",letterSpacing:0.5},children:"TIN T\u1EE8C"}),/*#__PURE__*/_jsx("img",{src:"/BoCongThuong.png",style:{marginTop:"50px",width:"200px",height:"76px"}})]})]}),/*#__PURE__*/_jsxs(Box,{display:'flex',flexDirection:'column',children:[/*#__PURE__*/_jsx(Box,{sx:{fontSize:"16px",fontFamily:"Roboto",fontWeight:"bold",color:"white",float:"left"},children:"CH\xCDNH S\xC1CH"}),/*#__PURE__*/_jsx(Divider,{style:{border:"1px solid white",