import * as React from "react";
import { styled, makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import { getProduct, getTypeProduct } from "../../redux/actions/admin";
import { RootState } from "../../redux/reducers";
import { Stack, Divider, AppBar, Box, Toolbar, Typography, Popover, MenuItem, Avatar, InputAdornment, OutlinedInput, IconButton, TextField } from '@mui/material';

import { MenuItem as MenuItemTypeProduct } from "@material-ui/core"
import { StyledMenuItem } from '../../layouts/navigation/style'
import { BoxSpan } from '../../layouts/navigation/style'

import { IProduct } from "../../redux/types/product";
import { ITypeProduct } from "../../redux/types/typeproduct";

import SearchIcon from '@mui/icons-material/Search';
import { Person, Notifications } from '@mui/icons-material';

import { Link, NavLink } from "react-router-dom";

const StyledSearch = styled(OutlinedInput)(({ theme }) => ({
    width: 240,
    '& fieldset': {
        borderWidth: `1px !important`,
        // borderColor: `${alpha(theme.palette.grey[500], 0.32)} !important`,
        borderColor: '#rgba(0, 0, 0, 0.87)'
    },
}));

const useStyles = makeStyles((theme) => ({
    textfield: {
        '& .MuiSelect-select': {
            color: 'black', fontSize: '12px'
        },
        '& .MuiSvgIcon-root': {
            fontSize: '20px', paddingRight: '2px'
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderRadius: '20px'
            },
            '&.Mui-focused fieldset': {
                border: "1px solid black",
            }
        },
        '& label.Mui-focused': {
            color: 'black',
        },
        '& fieldset': {
            borderRadius: '30px'
        },

    },
    hoverDetail: {
        '&: hover': {
            color: 'green',
        },
    },
}))

const StyledRoot = styled(AppBar)(() => ({
    boxShadow: 'none',
    width: '100%',
    backgroundColor: 'white',
    fontWeight: 'bold',
}));

const Guest: React.FC = (): JSX.Element => {

    const dispatch = useDispatch();
    const [anchorElUser, setAnchorElUser] = React.useState(null);

    const openUser = Boolean(anchorElUser);
    const handleClickUser = (event: any) => {
        setAnchorElUser(event.currentTarget);
    };
    const handleCloseUser = () => {
        setAnchorElUser(null);
    };
    const classes = useStyles();
    const [filterName, setFilterName] = React.useState('');

    const [filterNameTypeProduct, setFilterNameTypeProduct] = React.useState('');

    const [products, setProducts] = React.useState<IProduct[]>([]);
    const [typeProducts, setTypeProducts] = React.useState<ITypeProduct[]>([]);
    const admin = useSelector((state: RootState) => state.admin);

    React.useEffect(() => {
        dispatch(getProduct());
    }, [dispatch]);

    React.useEffect(() => {
        dispatch(getTypeProduct());
    }, [dispatch]);

    React.useEffect(() => {
        setProducts(() =>
            admin?.products?.filter((product: any) =>
                product.nameProduct
            ));
    }, [admin]);
    const findIndexEvent = admin.products.map((product: any) => product.nameProduct)
    React.useEffect(() => {
        setTypeProducts(() => admin?.typeproducts?.filter((typeproduct: any) => typeproduct.nameTypeProduct));
    }, [admin]);

    const myInputProps_TenTypeProduct = {
        startAdornment: <Box style={{ fontSize: '12px', width: '150px' }}
        > Loáº¡i Sáº£n Pháº©m</Box>,
        style: {
            height: '48px',
        }
    }

    const handleFilterByName = (event: any) => {
        const keyword = event.target.value;

        if (keyword !== '') {
            const results = admin?.products?.filter((product: any) => {
                return product.nameProduct.toLowerCase().startsWith(keyword.toLowerCase());
                // Use the toLowerCase() method to make it case-insensitive
            });
            setProducts(results);
        } else {
            setProducts(() => admin?.products?.filter((product: any) => product.nameProduct));
        }

        setFilterName(keyword);
    };

    const handleFilterByNameTypeProduct = (event: any) => {
        const keyword = event.target.value;

        if (keyword !== '') {
            const results = admin?.products?.filter((product: any) => {
                return product.typeProduct.nameTypeProduct.toLowerCase().startsWith(keyword.toLowerCase());
                // Use the toLowerCase() method to make it case-insensitive
            });
            setProducts(results);
        } else {
            setProducts(() => admin?.products?.filter((product: any) => product.typeProduct.nameTypeProduct));
        }

        setFilterNameTypeProduct(keyword);
    };

    React.useEffect(() => {
        document.title = "Trang Chá»§";
    }, []);

    return (
        <Box>
            <Box>
                <StyledRoot style={{ boxShadow: "none", overflowX: "hidden" }}>
                    <Toolbar>
                        <Link style={{ textDecoration: 'none' }} to={'/user'}>
                            <img src="/logoBM.png" style={{ height: "90px", width: "230px" }}></img>
                        </Link>
                        <Box textAlign={"center"} sx={{ flexGrow: 1 }}>
                            <StyledSearch
                                style={{ borderRadius: '20px', fontSize: '13px', height: "48px", marginRight: '16px' }}
                                value={filterName}
                                onChange={handleFilterByName}
                                placeholder="TÃ¬m kiáº¿m sáº£n pháº©m..."
                                startAdornment={
                                    <InputAdornment position="start" style={{ paddingLeft: 1.3 }}>
                                        <SearchIcon style={{ width: '16px' }} sx={{ color: 'rgba(0, 0, 0, 0.87)' }} />
                                    </InputAdornment>
                                }
                            />
                            <TextField
                                id="filled-select-currency"
                                select
                                value={filterNameTypeProduct}
                                onChange={handleFilterByNameTypeProduct}
                                variant="outlined"
                                InputProps={myInputProps_TenTypeProduct}
                                style={{ width: 240 }}
                                className={classes.textfield}
                            >
                                {typeProducts.map((typeProduct) => (
                                    <MenuItemTypeProduct key={typeProduct._id}
                                        value={typeProduct.nameTypeProduct}
                                        style={{ fontSize: "12px" }}
                                    >
                                        {typeProduct.nameTypeProduct}
                                    </MenuItemTypeProduct>
                                ))}
                            </TextField>
                        </Box>
                        {/* <Box>
                            <Notifications style={{ color: 'black' }} />
                        </Box> */}
                    </Toolbar>
                </StyledRoot>
            </Box>
            <Box style={{
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'center'
            }}>
                <Box style={{ display: "flex", flexDirection: "row" }} >
                    {products && products.length > 0 ? (
                        <Box style={{ display: "flex", flexDirection: "row" }}>
                            <Box style={{ paddingLeft: '20px', top: 100, zIndex: 10, }}>
                                {products.map((product: any) =>
                                    <Box>
                                        {product.nameProduct}
                                    </Box>
                                )}
                            </Box>
                        </Box>
                    ) : (
                        <Box style={{ margin: "70px 0px" }}>
                            <Box>
                                <img style={{ width: "400px", display: "flex", margin: "auto" }} src="/not-found.png" />
                            </Box>
                            <Box>
                                <Typography style={{ fontSize: "45px", fontWeight: "bold" }}>
                                    KhÃ´ng Tá»n Táº¡i Sáº£n Pháº©m
                                </Typography>
                            </Box>
                        </Box>
                    )}

                </Box >
            </Box>
        </Box >
    );
};

export default Guest;
