import React from 'react';
import { styled, makeStyles } from "@material-ui/core/styles";
import { useDispatch, useSelector } from "react-redux";
import { getProduct, getTypeProduct } from "../../redux/actions/admin";
import { RootState } from "../../redux/reducers";
import { Stack, Divider, AppBar, Box, Toolbar, Typography, Popover, MenuItem, Avatar, InputAdornment, OutlinedInput, IconButton, TextField } from '@mui/material';

import { Link } from "react-router-dom";
import { MenuItem as MenuItemTypeProduct } from "@material-ui/core";
import "./SlideShow.css";
import { data } from "./data"

import { IProduct } from "../../redux/types/product";
import { ITypeProduct } from "../../redux/types/typeproduct";

import SearchIcon from '@mui/icons-material/Search';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

const StyledSearch = styled(OutlinedInput)(({ theme }) => ({
    width: 240,
    borderRadius: '20px',
    fontSize: '13px',
    height: "42px",
    marginRight: '16px',
    backgroundColor: "white",
    marginTop: "4px",
    '& fieldset': {
        borderWidth: `1px !important`,
        // borderColor: `${alpha(theme.palette.grey[500], 0.32)} !important`,
        borderColor: '#rgba(0, 0, 0, 0.87)'
    },
}));

const useStyles = makeStyles((theme) => ({
    textfield: {
        '& .MuiSelect-select': {
            color: 'black', fontSize: '12px'
        },
        '& .MuiSvgIcon-root': {
            fontSize: '20px', paddingRight: '2px'
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderRadius: '20px',
            },
            '&.Mui-focused fieldset': {
                border: "1px solid black",
            }
        },
        '& label.Mui-focused': {
            color: 'black',
        },
        '& fieldset': {
            borderRadius: '30px',
            height: "46px"
        },

    },
    hoverDetail: {
        '&: hover': {
            color: 'green',
        },
    },
}))

const StyledRoot = styled(AppBar)(() => ({
    boxShadow: 'none',
    width: '100%',
    backgroundColor: 'white',
    fontWeight: 'bold',
}));

const Guest: React.FC = (): JSX.Element => {

    const dispatch = useDispatch();
    const [anchorElUser, setAnchorElUser] = React.useState(null);
    const [slide, setSlide] = React.useState(0);

    const openUser = Boolean(anchorElUser);
    const handleClickUser = (event: any) => {
        setAnchorElUser(event.currentTarget);
    };
    const handleCloseUser = () => {
        setAnchorElUser(null);
    };
    const classes = useStyles();
    const [filterName, setFilterName] = React.useState('');

    const [filterNameTypeProduct, setFilterNameTypeProduct] = React.useState('');

    const [products, setProducts] = React.useState<IProduct[]>([]);
    const [typeProducts, setTypeProducts] = React.useState<ITypeProduct[]>([]);
    const admin = useSelector((state: RootState) => state.admin);

    const nextSlide = () => {
        setSlide(slide === data.length - 1 ? 0 : slide + 1);
    };

    const prevSlide = () => {
        setSlide(slide === 0 ? data.length - 1 : slide - 1);
    };


    React.useEffect(() => {
        dispatch(getProduct());
    }, [dispatch]);

    React.useEffect(() => {
        dispatch(getTypeProduct());
    }, [dispatch]);

    React.useEffect(() => {
        setProducts(() =>
            admin?.products?.filter((product: any) =>
                product.nameProduct
            ));
    }, [admin]);
    const findIndexEvent = admin.products.map((product: any) => product.nameProduct)
    React.useEffect(() => {
        setTypeProducts(() => admin?.typeproducts?.filter((typeproduct: any) => typeproduct.nameTypeProduct));
    }, [admin]);

    const myInputProps_TenTypeProduct = {
        startAdornment: <Box style={{ fontSize: '12px', width: '150px', marginTop: "-4px" }}
        > Loại Sản Phẩm</Box>,
        style: {
            height: '48px',
        }
    }

    const handleFilterByName = (event: any) => {
        const keyword = event.target.value;

        if (keyword !== '') {
            const results = admin?.products?.filter((product: any) => {
                return product.nameProduct.toLowerCase().startsWith(keyword.toLowerCase());
                // Use the toLowerCase() method to make it case-insensitive
            });
            setProducts(results);
        } else {
            setProducts(() => admin?.products?.filter((product: any) => product.nameProduct));
        }

        setFilterName(keyword);
    };

    const handleFilterByNameTypeProduct = (event: any) => {
        const keyword = event.target.value;

        if (keyword !== '') {
            const results = admin?.products?.filter((product: any) => {
                return product.typeProduct.nameTypeProduct.toLowerCase().startsWith(keyword.toLowerCase());
                // Use the toLowerCase() method to make it case-insensitive
            });
            setProducts(results);
        } else {
            setProducts(() => admin?.products?.filter((product: any) => product.typeProduct.nameTypeProduct));
        }

        setFilterNameTypeProduct(keyword);
    };

    React.useEffect(() => {
        document.title = "Trang Chủ";
    }, []);
    return (
        <Box>
            <Box>
                <StyledRoot style={{ boxShadow: "none", overflowX: "hidden", backgroundImage: "url(/backgroundHeader.jpg)", height: "110px" }}>
                    <Toolbar>
                        <Link style={{ textDecoration: 'none', marginTop: "10px" }} to={'/user'}>
                            <img src="/logoBM.png" style={{ height: "90px", width: "90px" }}></img>
                        </Link>
                        <Box textAlign={"center"} display={'flex'} flexDirection={'column'} flexGrow={1}>
                            <Typography style={{ color: "#dd3333", fontWeight: 700, fontStyle: "normal", fontSize: "30px" }}>
                                BÌNH MINH GLOBAL
                            </Typography>
                            <Typography style={{ color: "#0066bf", fontSize: "19px", fontWeight: 700, fontStyle: "normal" }}>
                                NHÀ CUNG CẤP SỐ 1 VỀ BỒN NƯỚC VÀ NĂNG LƯỢNG MẶT TRỜI
                            </Typography>
                        </Box>
                        <Box display={'flex'} flexDirection={'row'}>
                            <Box component={"img"} src="/call.gif" style={{ width: "45px", height: "45px", paddingRight: "15px" }} />
                            <Box display={'flex'} flexDirection={'column'}>
                                <Typography style={{ fontSize: "14px", color: "black", fontWeight: "bold" }}>
                                    Hotline:
                                </Typography>
                                <Typography style={{ fontSize: "16px", color: "#ff0000", fontWeight: "bold" }}>
                                    0362.526.678
                                </Typography>
                            </Box>
                        </Box>
                    </Toolbar>
                </StyledRoot>
                <StyledRoot style={{ backgroundColor: "#0066bf", marginTop: "110px", height: "50px" }}>
                    <Box display={'flex'} flexDirection={'row'} sx={{ padding: "0px 170px" }}>
                        <Box display={'flex'} flexDirection={'row'} flexGrow={1} sx={{ fontSize: "16px", fontFamily: "Roboto" }}>
                            <Box sx={{
                                padding: "0px 25px", height: "33px", paddingTop: "17px", backgroundColor: "white", color: "#0066bf"
                            }}>TRANG CHỦ</Box>
                            <Box sx={{
                                padding: "0px 25px", paddingTop: "17px",
                                '&: hover': {
                                    backgroundColor: "white",
                                    color: "#0066bf"
                                },
                            }}>SẢN PHẨM</Box>
                            <Box sx={{
                                padding: "0px 25px", paddingTop: "17px",
                                '&: hover': {
                                    backgroundColor: "white",
                                    color: "#0066bf"
                                },
                            }}>TIN TỨC</Box>
                        </Box>
                        <Box>
                            <StyledSearch
                                value={filterName}
                                onChange={handleFilterByName}
                                placeholder="Tìm kiếm sản phẩm..."
                                startAdornment={
                                    <InputAdornment position="start" style={{ paddingLeft: 1.3 }}>
                                        <SearchIcon style={{ width: '16px' }} sx={{ color: 'rgba(0, 0, 0, 0.87)' }} />
                                    </InputAdornment>
                                }
                            />
                            <TextField
                                id="filled-select-currency"
                                select
                                value={filterNameTypeProduct}
                                onChange={handleFilterByNameTypeProduct}
                                variant="outlined"
                                InputProps={myInputProps_TenTypeProduct}
                                style={{ width: 270, backgroundColor: "white", borderRadius: "30px", height: "42px", marginTop: "4px" }}
                                className={classes.textfield}
                            >
                                {typeProducts.map((typeProduct) => (
                                    <MenuItemTypeProduct key={typeProduct._id}
                                        value={typeProduct.nameTypeProduct}
                                        style={{ fontSize: "12px" }}
                                    >
                                        {typeProduct.nameTypeProduct}
                                    </MenuItemTypeProduct>
                                ))}
                            </TextField>
                        </Box>
                    </Box>
                </StyledRoot>
            </Box>
            <Box className="carousel">
                <ArrowBackIosIcon onClick={prevSlide} className="arrow arrow-left" style={{ color: "grey" }} />
                {data.map((item, idx) => {
                    return (
                        <img
                            src={item.src}
                            alt={item.alt}
                            key={idx}
                            className={slide === idx ? "slide" : "slide slide-hidden"}
                        />
                    );
                })}
                <ArrowForwardIosIcon
                    onClick={nextSlide}
                    className="arrow arrow-right"
                    style={{ color: "grey" }}
                />
                <span className="indicators">
                    {data.map((_, idx) => {
                        return (
                            <button
                                key={idx}
                                className={
                                    slide === idx ? "indicator" : "indicator indicator-inactive"
                                }
                                onClick={() => setSlide(idx)}
                            ></button>
                        );
                    })}
                </span>
            </Box>
            <Box sx={{textAlign:"center", marginTop:"50px"}}>
                <Typography style={{ fontFamily: "Roboto", fontSize: "36px", color: "#344767" }}>
                    SẢN PHẨM CỦA CHÚNG TÔI
                </Typography>
            </Box>

            <Box style={{
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'center'
            }}>
                <Box style={{ display: "flex", flexDirection: "row", paddingTop: "100px" }} >
                    {products && products.length > 0 ? (
                        <Box style={{ display: "flex", flexDirection: "row" }}>
                            <Box sx={{ paddingLeft: '20px', top: 100, zIndex: 10 }}>
                                {products.map((product: any) =>
                                    <Box>
                                        {product.nameProduct}
                                    </Box>
                                )}
                            </Box>
                        </Box>
                    ) : (
                        <Box style={{ margin: "70px 0px" }}>
                            <Box>
                                <img style={{ width: "400px", display: "flex", margin: "auto" }} src="/not-found.png" />
                            </Box>
                            <Box>
                                <Typography style={{ fontSize: "45px", fontWeight: "bold" }}>
                                    Không Tồn Tại Sản Phẩm
                                </Typography>
                            </Box>
                        </Box>
                    )}

                </Box >
            </Box>
        </Box >
    );
};

export default Guest;
