import types from "../actions/types";
import { IUser } from "../types/user";
import { IRole } from "../../redux/types/role";
import { IProduct } from "../types/product";
import { ITypeProduct } from "../types/typeproduct";
import { IUserState, UserActions } from "../types/user";

const initialState: IUserState = {
    token: localStorage.getItem("user__token"),
    loading: true,
    isAuthenticated: null,
    products: [] as IProduct[],
    typeproducts: [] as ITypeProduct[],
    user: {} as IUser,
    getRole: {} as IRole,
};

const userReducer = (
    state = initialState,
    action: UserActions
): IUserState => {
    switch (action.type) {
        case types.USER_LOADED:
            return {
                ...state,
                ...action.payload,
                isAuthenticated: true,
                loading: false,
            };
        case types.GET_PRODUCT:
            return {
                ...state,
                products: action.payload,
            };

        case types.GET_TYPEPRODUCT:
            return {
                ...state,
                typeproducts: action.payload,
            };
        case types.USER_AUTH_ERROR:
            localStorage.removeItem("user__token");
            return {
                ...state,
                token: null,
                isAuthenticated: false,
                loading: false,
                user: {} as IUser,
            };
    }
};

export default userReducer;
